function bubbleSort(arr) {
    for(let i = 0; i < arr.length; i++) {
        for(let j = 0; j < arr.length-i-1; j++) {
            if(arr[j] > arr[j+1]) {
                [arr[j], arr[j+1]] = [arr[j+1], arr[j]];
            }
        }
    }
    return arr;
}

function twoSum(nums, target) {
    const map = new Map();
    for(let i = 0; i < nums.length; i++) {
        const complement = target - nums[i];
        if(map.has(complement)) {
            return [map.get(complement), i];
        }
        map.set(nums[i], i);
    }
    return [];
}

function binarySearch(nums, target) {
    let left = 0, right = nums.length - 1;
    while(left <= right) {
        const mid = left + Math.floor((right - left) / 2);
        if(nums[mid] === target) return mid;
        if(nums[mid] < target) left = mid + 1;
        else right = mid - 1;
    }
    return -1;
}

function longestPalindrome(s) {
    let start = 0, maxLength = 0;

    function expandAroundCenter(left, right) {
        while(left >= 0 && right < s.length && s[left] === s[right]) {
            const currLength = right - left + 1;
            if(currLength > maxLength) {
                start = left;
                maxLength = currLength;
            }
            left--;
            right++;
        }
    }

    for(let i = 0; i < s.length; i++) {
        expandAroundCenter(i, i);   // odd length
        expandAroundCenter(i, i+1); // even length
    }

    return s.substring(start, start + maxLength);
}

function reverseList(head) {
    let prev = null;
    let curr = head;

    while(curr !== null) {
        let next = curr.next;
        curr.next = prev;
        prev = curr;
        curr = next;
    }

    return prev;
}

function generateParenthesis(n) {
    const result = [];

    function backtrack(open, closed, str) {
        if(str.length === 2*n) {
            result.push(str);
            return;
        }

        if(open < n) backtrack(open+1, closed, str+'(');
        if(closed < open) backtrack(open, closed+1, str+')');
    }

    backtrack(0, 0, '');
    return result;
}

function hammingWeight(n) {
    let count = 0;
    while(n !== 0) {
        n &= (n - 1);
        count++;
    }
    return count;
}

function isValid(s) {
    const stack = [];
    const map = {
        '(': ')',
        '{': '}',
        '[': ']'
    };

    for(let char of s) {
        if(map[char]) {
            stack.push(char);
        } else {
            if(map[stack.pop()] !== char) return false;
        }
    }

    return stack.length === 0;
}

function mergeKLists(lists) {
    const minHeap = new MinHeap();

    // Add first elements
    for(let i = 0; i < lists.length; i++) {
        if(lists[i]) minHeap.push([lists[i].val, i]);
    }

    const dummy = new ListNode(0);
    let curr = dummy;

    while(!minHeap.isEmpty()) {
        const [val, listIndex] = minHeap.pop();
        curr.next = lists[listIndex];
        curr = curr.next;
        lists[listIndex] = lists[listIndex].next;

        if(lists[listIndex]) {
            minHeap.push([lists[listIndex].val, listIndex]);
        }
    }

    return dummy.next;
}

